INSERT INTO `quotas` VALUES (58, 'GS_ENT_ABNORMAL_STATE', 'ENT_ABNORMAL_STATE', '企业经营状态', 'QUOTA', 'select regstatus as keyvalue from std_ent_basic_list where req_id = ?', 'SQL', 'Y', 2, 12, 37, '当前', '企业经营异常状态', NULL, NULL, NULL, NULL);
INSERT INTO `quotas` VALUES (59, 'GS_NUM_COMMERCIAL_PENALTIES', 'NUM_COMMERCIAL_PENALTIES', '严重工商处罚次数', 'QUOTA', 'select count(1) as keyvalue from std_ent_caseinfo_list\r\nwhere penresult is not null\r\nand ((pendecissdate is null and publicdate is null) or \r\n(timestampdiff(month,greatest(pendecissdate, publicdate) ,curdate()) <= 12 and pendecissdate is not null and publicdate is not null) or\r\n(timestampdiff(month,ifnull(pendecissdate, publicdate) ,curdate()) <= 12 and (pendecissdate is not null or publicdate is not null))\r\n) and (penresult like \'%吊销%\' or penresult like \'%停产%\' or penresult like \'%停业%\' or penresult like \'%偷税%\')\r\nand req_id = ?', 'SQL', 'Y', 2, 12, 39, '近12个月', '严重工商处罚次数', NULL, NULL, NULL, NULL);
INSERT INTO `quotas` VALUES (62, 'GS_NUM_SHARES_FROZEN', 'NUM_SHARES_FROZEN', '股权冻结笔数', 'QUOTA', 'select count(distinct frodocno) as keyvalue from std_ent_sharesfrost_list \r\nwhere frodocno is not null \r\nand (frofrom is not null and froto is not null and thawdate is not null) \r\nand timestampdiff(month,GREATEST(frofrom,ifnull(thawdate, froto)) ,curdate()) <=12\r\nand req_id = ?', 'SQL', 'Y', 2, 12, 38, '近12个月', '股权冻结笔数', NULL, NULL, NULL, NULL);
INSERT INTO `quotas` VALUES (63, 'GS_NUM_ABNORMAL_DIRECTORY', 'NUM_ABNORMAL_DIRECTORY', '异常名录记录数', 'QUOTA', 'select count(1) as keyvalue from std_ent_exceptions see, std_ent_basic_list sebl\r\nwhere see.creditcode is not null\r\nand see.creditcode = sebl.creditcode\r\nand see.outdate is null\r\nand see.req_id = sebl.req_id\r\nand see.req_id = ?', 'SQL', 'Y', 2, 12, 38, '当前', '异常名录记录数', NULL, NULL, NULL, NULL);
