SELECT DISTINCT trim(v.code) code,trim(v.description) description,trim(v.rank) rank FROM eds_ss_add_result_regex r JOIN eds_ss_add_result_values v ON v.description=r.result WHERE r.enable='Y' AND r.name IN
#{item}
AND v.rank =( select t.rank from ( SELECT v1.rank FROM eds_ss_add_result_regex r2 LEFT JOIN eds_ss_add_result_values v1 ON v1.description=r2.result WHERE r2.enable='Y' AND r2.name IN
#{item}
GROUP BY v1.rank ORDER BY v1.rank ) t
LIMIT 1 )
