#{item}
AND v."RANK" =( select t."RANK" from ( SELECT v1."RANK" FROM EDS_SS_ADD_RESULT_REGEX r2 LEFT JOIN EDS_SS_ADD_RESULT_VALUES v1 ON v1.DESCRIPTION=r2.RESULT WHERE r2."ENABLE"='Y' AND r2."NAME" IN
#{item}
GROUP BY v1."RANK" ORDER BY v1."RANK" ) t
where ROWNUM=1 )
